implementation.source:

float3 standardShaderDiffuse(
        standardShaderLightingData lightingData,
        float3 L,
        float3 N,
        float3 diffuse,
        float Kd)
{
    float NDL = saturate(dot(N, L));
    if (NDL > 0.0f)
        return diffuse * Kd * NDL;
    else
        return float3(0.0f, 0.0f, 0.0f);
}


