float4 textureMapping(
                float2 uv, 
                texture2D map, 
                sampler2D mapSampler,
                float soffset,
                float toffset,
                float sscale,
                float tscale,
                bool sflip,
                bool tflip,
                bool swapSt)
{
    float2 texUV = uv * float2(sscale, tscale) + float2(soffset, toffset);

    if(sflip)
        texUV.x *= -1;
    if(tflip)
        texUV.y *= -1;
    if(swapSt)
        texUV.xy = texUV.yx;

	texUV.y = 1.0f - texUV.y;
	float4 color = tex2D(mapSampler, texUV);
	return color;
}


float4 textureMapping(
                float2 uv, 
                Texture2D map, 
                sampler mapSampler,
                float soffset,
                float toffset,
                float sscale,
                float tscale,
                bool sflip,
                bool tflip,
                bool swapSt)
{
    float2 texUV = uv * float2(sscale, tscale) + float2(soffset, toffset);

    if(sflip)
        texUV.x *= -1;
    if(tflip)
        texUV.y *= -1;
    if(swapSt)
        texUV.xy = texUV.yx;

	texUV.y = 1.0f - texUV.y;
	float4 color = map.Sample(mapSampler, texUV);
	return color;
}


vec4 textureMapping(
                vec2 uv, 
                sampler2D mapSampler,
                float soffset,
                float toffset,
                float sscale,
                float tscale,
                bool sflip,
                bool tflip,
                bool swapSt)
{
    vec2 texUV = uv * vec2(sscale, tscale) + vec2(soffset, toffset);

    if(sflip)
        texUV.x *= -1;
    if(tflip)
        texUV.y *= -1;
    if(swapSt)
        texUV.xy = texUV.yx;

	texUV.y = 1.0f - texUV.y;
	vec4 color = texture(mapSampler, texUV);
	return color;
}
